import React, {useState} from 'react'
import './Title.css';
import WebcamCapture from './WebcamCapture';
import { Link } from 'react-router-dom';
import Popup from './Popup';

function Title() {
  const popupRef = React.useRef();
  const [response, setResponse] = useState(null);

  const handleResponse = (data) => {
    setResponse(data);
    popupRef.current.setModalIsOpen(true);
  }

  return (
    <div>
      <header className="Header">
        <h1 className="Header-title">ROCK-PAPER-SCISSORS</h1>
        <div className="Header-buttons">
          <Link to="/About">
            <button className="Header-button">About</button>
          </Link>
          <Link to="/">
            <button className="Header-button">Mode Selection</button>
          </Link>
          <a href="https://ece.ubc.ca/" target="_blank" rel="noopener noreferrer">
            <button className="Header-button ubc-ece-button">UBC ECE</button>
          </a>
        </div>
      </header>
      <WebcamCapture onResponse={handleResponse}/>
      <button onClick={() => popupRef.current.setModalIsOpen(true)}>Show Result</button>
      <Popup ref={popupRef} response={response} />
    </div>
  );
}

export default Title
